mapper.update:
UPDATE mubs_adminuser


        login = #{update.login},


        password = #{update.password},


        creator = #{update.creator},


        createDate = #{update.createDate},


        lastModifier = #{update.lastModifier},


        lastModDate = #{update.lastModDate},


        status = #{update.status},


        phone = #{update.phone},


        email = #{update.email},





    AND id = #{condition.id}


    AND login = #{condition.login}


    AND password = #{condition.password}


    AND creator = #{condition.creator}


    AND createDate = #{condition.createDate}


    AND lastModifier = #{condition.lastModifier}


    AND lastModDate = #{condition.lastModDate}


    AND status = #{condition.status}


    AND phone = #{condition.phone}


    AND email = #{condition.email}

UPDATE mubs_adminuser
login = #{login}, password = #{password}, creator = #{creator}, createDate = #{createDate}, lastModifier = #{lastModifier}, lastModDate = #{lastModDate}, status = #{status}, phone = #{phone}, email = #{email},
WHERE id = #{id}

