mapper.select:
        SELECT



                    , id


                    , login as name


                    , password


                    , creator


                    , createDate


                    , lastModifier


                    , lastModDate


                    , status


                    , phone


                    , email







        FROM mubs_adminuser




                ${condition.groupOp}   ${condition.id.field}  ${condition.id.op} #{condition.id.data}            

                AND id like CONCAT('%', #{condition.id} , '%')


                AND id like CONCAT(#{condition.id} , '%')


                #{listItem}





                ${condition.groupOp}   ${condition.login.field}  ${condition.login.op} #{condition.login.data}            

                AND login like CONCAT('%', #{condition.login.data} , '%')


                AND login like CONCAT(#{condition.login.data} , '%')


                 ${condition.groupOp}   ${condition.login.field}  ${condition.login.op}
                #{listItem}



            ${condition.groupOp}   ${condition.password.field}  ${condition.password.op}

#{condition.password.data}            

                AND password like CONCAT('%', #{condition.password} , '%')


                AND password like CONCAT(#{condition.password} , '%')


                #{listItem}



            ${condition.groupOp}   ${condition.creator.field}  ${condition.creator.op}

#{condition.creator.data}            

                AND creator like CONCAT('%', #{condition.creator} , '%')


                AND creator like CONCAT(#{condition.creator} , '%')


                #{listItem}



            ${condition.groupOp}   ${condition.createDate.field}  ${condition.createDate.op}

#{condition.createDate.data}            

                AND createDate like CONCAT('%', #{condition.createDate} , '%')


                AND createDate like CONCAT(#{condition.createDate} , '%')


                #{listItem}



            ${condition.groupOp}   ${condition.lastModifier.field}  ${condition.lastModifier.op}

#{condition.lastModifier.data}            

                AND lastModifier like CONCAT('%', #{condition.lastModifier} , '%')


                AND lastModifier like CONCAT(#{condition.lastModifier} , '%')


                #{listItem}



            ${condition.groupOp}   ${condition.lastModDate.field}  ${condition.lastModDate.op}

#{condition.lastModDate.data}            

                AND lastModDate like CONCAT('%', #{condition.lastModDate} , '%')


                AND lastModDate like CONCAT(#{condition.lastModDate} , '%')


                #{listItem}



            ${condition.groupOp}   ${condition.status.field}  ${condition.status.op}

#{condition.status.data}            

                AND status like CONCAT('%', #{condition.status} , '%')


                AND status like CONCAT(#{condition.status} , '%')


                #{listItem}



            ${condition.groupOp}   ${condition.phone.field}  ${condition.phone.op}

#{condition.phone.data}            

                AND phone like CONCAT('%', #{condition.phone} , '%')


                AND phone like CONCAT(#{condition.phone} , '%')


                #{listItem}



            ${condition.groupOp}   ${condition.email.field}  ${condition.email.op}

#{condition.email.data}            

                AND email like CONCAT('%', #{condition.email} , '%')


                AND email like CONCAT(#{condition.email} , '%')


                #{listItem}





        ORDER BY

            ${sort.field} ${sort.order}
