mapper.update:
UPDATE mubs_resource_action


        resourceId = #{update.resourceId},


        divId = #{update.divId},


        actionScript = #{update.actionScript},


        name = #{update.name},


        actionAlias = #{update.actionAlias},


        creator = #{update.creator},


        createDate = #{update.createDate},


        lastModifier = #{update.lastModifier},


        lastModDate = #{update.lastModDate},


        description = #{update.description},





    AND id = #{condition.id}


    AND resourceId = #{condition.resourceId}


    AND divId = #{condition.divId}


    AND actionScript = #{condition.actionScript}


    AND name = #{condition.name}


    AND actionAlias = #{condition.actionAlias}


    AND creator = #{condition.creator}


    AND createDate = #{condition.createDate}


    AND lastModifier = #{condition.lastModifier}


    AND lastModDate = #{condition.lastModDate}


    AND description = #{condition.description}

UPDATE mubs_resource_action
resourceId = #{resourceId}, divId = #{divId}, actionScript = #{actionScript}, name = #{name}, actionAlias = #{actionAlias}, creator = #{creator}, createDate = #{createDate}, lastModifier = #{lastModifier}, lastModDate = #{lastModDate}, description = #{description},
WHERE id = #{id}

