mapper.update:
UPDATE mubs_role


        name = #{update.name},


        creator = #{update.creator},


        createDate = #{update.createDate},


        lastModifier = #{update.lastModifier},


        lastModDate = #{update.lastModDate},


        description = #{update.description},





    AND id = #{condition.id}


    AND name = #{condition.name}


    AND creator = #{condition.creator}


    AND createDate = #{condition.createDate}


    AND lastModifier = #{condition.lastModifier}


    AND lastModDate = #{condition.lastModDate}


    AND description = #{condition.description}

UPDATE mubs_role
name = #{name}, creator = #{creator}, createDate = #{createDate}, lastModifier = #{lastModifier}, lastModDate = #{lastModDate}, description = #{description},
WHERE id = #{id}

mapper.select:
SELECT



            , id


            , name


            , creator


            , createDate


            , lastModifier


            , lastModDate


            , description







        FROM mubs_role




        ${condition.groupOp}   ${condition.id.field}  ${condition.id.op} #{condition.id.data}


        AND id like CONCAT('%', #{condition.id.data} , '%')


        AND id like CONCAT(#{condition.id.data} , '%')


        ${condition.groupOp}   ${condition.id.field}  ${condition.id.op}
        #{listItem}





        ${condition.groupOp}   ${condition.name.field}  ${condition.name.op} #{condition.name.data}


        AND name like CONCAT('%', #{condition.name.data} , '%')


        AND name like CONCAT(#{condition.name.data} , '%')


        ${condition.groupOp}   ${condition.name.field}  ${condition.name.op}
        #{listItem}





        ${condition.groupOp}   ${condition.creator.field}  ${condition.creator.op} #{condition.creator.data}


        AND creator like CONCAT('%', #{condition.creator.data} , '%')


        AND creator like CONCAT(#{condition.creator.data} , '%')


        ${condition.groupOp}   ${condition.creator.field}  ${condition.creator.op}
        #{listItem}





        ${condition.groupOp}   ${condition.createDate.field}  ${condition.createDate.op} #{condition.createDate.data}


        AND createDate like CONCAT('%', #{condition.createDate.data} , '%')


        AND createDate like CONCAT(#{condition.createDate.data} , '%')


        ${condition.groupOp}   ${condition.createDate.field}  ${condition.createDate.op}
        #{listItem}





        ${condition.groupOp}   ${condition.lastModifier.field}  ${condition.lastModifier.op} #{condition.lastModifier.data}


        AND lastModifier like CONCAT('%', #{condition.lastModifier.data} , '%')


        AND lastModifier like CONCAT(#{condition.lastModifier.data} , '%')


        ${condition.groupOp}   ${condition.lastModifier.field}  ${condition.lastModifier.op}
        #{listItem}





        ${condition.groupOp}   ${condition.lastModDate.field}  ${condition.lastModDate.op} #{condition.lastModDate.data}


        AND lastModDate like CONCAT('%', #{condition.lastModDate.data} , '%')


        AND lastModDate like CONCAT(#{condition.lastModDate.data} , '%')


        ${condition.groupOp}   ${condition.lastModDate.field}  ${condition.lastModDate.op}
        #{listItem}





        ${condition.groupOp}   ${condition.description.field}  ${condition.description.op} #{condition.description.data}


        AND description like CONCAT('%', #{condition.description.data} , '%')


        AND description like CONCAT(#{condition.description.data} , '%')


        ${condition.groupOp}   ${condition.description.field}  ${condition.description.op}
        #{listItem}





ORDER BY

    ${sort.field} ${sort.order}

LIMIT 0,1

