mapper.update:
UPDATE mubs_user_data


        creator = #{update.creator},


        createDate = #{update.createDate},


        lastModifier = #{update.lastModifier},


        lastModDate = #{update.lastModDate},


        status = #{update.status},


        dataModelId = #{update.dataModelId},


        dataId = #{update.dataId},


        userId = #{update.userId},





    AND id = #{condition.id}


    AND creator = #{condition.creator}


    AND createDate = #{condition.createDate}


    AND lastModifier = #{condition.lastModifier}


    AND lastModDate = #{condition.lastModDate}


    AND status = #{condition.status}


    AND dataModelId = #{condition.dataModelId}


    AND dataId = #{condition.dataId}


    AND userId = #{condition.userId}

UPDATE mubs_user_data
creator = #{creator}, createDate = #{createDate}, lastModifier = #{lastModifier}, lastModDate = #{lastModDate}, status = #{status}, dataModelId = #{dataModelId}, dataId = #{dataId}, userId = #{userId},
WHERE id = #{id}

